import React, { Component } from "react";
import { Card } from "react-bootstrap";
import './style.css';
import DeleteBtn from "../../components/DeleteBtn"

export default class BookSpine extends Component {

    constructor(props) {
        super(props);
    }

    colors = ["#D79922", "#EFE2BA", "#F13C20", "4056A1", "C5CBE3"]

    render() {
        let i = Math.floor( Math.random() * 5 ) + 1
        const color = colors
        return (

            <Card className="bookBox">
                <Card.Body key={this.props._id}>
                    <Card.Title> 
                        {this.props.title} by {this.props.authors}
                    </Card.Title>
                    <DeleteBtn onClick= {this.props.handleDelete} />
                </Card.Body>
            </Card>
        )    
    }
}
